function update(data) {
    $.each(data.pings, function(i, ping) {
        if (ping.timestamp != last_ping.timestamp && ping.origin != "unknown") {
            console.log('Ping from ' + ping.origin.country);

            pings.push(ping);

            let alpha3 = iso3166_alpha2_to_alpha3[ping.origin.country]; // GeoJSON uses ISO3166-Alpha3 codes while everybody else uses ISO3166-Alpha2 like reasonable people
            let n = 0;
            if (!(alpha3 in countries)) {
                n = 1;
                countries[alpha3] = 1;
            } else {
                n = countries[alpha3] + 1;
                countries[alpha3]++;
            }

            // Update Choropleth

            newChoropleth = {};
            newChoropleth[alpha3] = n;
            map.updateChoropleth(newChoropleth);

            // Create/Update arc

            let strokeWidth = 1.0;
            $.each(pings, function(i, p) {
                if (p.origin.lat === ping.origin.lat && p.origin.lon === ping.origin.lon) {
                    strokeWidth += 1.5;
                }
            });

            arcs.push({
                origin: {
                    latitude: ping.origin.lat,
                    longitude: ping.origin.lon
                },
                destination: {
                    latitude: 50.85,
                    longitude: 4.35
                },
                strokeWidth: strokeWidth
            });

            map.arc(arcs, {
                animationSpeed: 1200
            })
        }
    });

    last_ping = data.pings[data.pings.length - 1];

    url = '/ssh-pings-backend/since/' + last_ping.timestamp;

    window.setTimeout(function () {
        $.ajax({
            dataType: 'json',
            url: url,
            success: update
        })
        // $.getJSON(url, update);
    }, 1000);
}

var map = new Datamap({
    element: document.getElementById('container'),
    responsive: true,
    fills: {
        HIGH: 'rgb(255, 69, 23)',
        MEDIUM: 'rgb(255, 69, 23)',
        LOW: 'rgb(230, 230, 230)',
        UNKNOWN: 'rgb(128, 128, 128)',
        defaultFill: 'rgb(230, 230, 230)'
    }
});

$(window).on('resize', function() {
    map.resize();
});

var pings = [];
var last_ping = {};
var countries = {};
var arcs = [];
$.getJSON("/ssh-pings-backend/last", function(data) {
    last_ping = data;

    window.setTimeout(function () {
        $.getJSON('/ssh-pings-backend/since/' + last_ping.timestamp, update);
    }, 1000);
})
